def calculate_fibonacci(n):
    """
    Calcule la suite de Fibonacci de manière récursive.
    Attention : inefficace pour les grands nombres.
    """
    if n <= 1:
        return n
    else:
        return calculate_fibonacci(n-1) + calculate_fibonacci(n-2)

# Générer une longue liste
result = [calculate_fibonacci(i) for i in range(30)]
print(result)
